import 'dart:async';import 'package:uuid/uuid.dart';import 'models/models.dart';// Ici on peut query depuis un backendclass UserRepository {  User? _user;  // Mock qui simule le fetch d'un User  Future<User?> getUser() async {    if (_user != null) return _user;    return Future.delayed(      const Duration(milliseconds: 300),          () => _user = User(const Uuid().v4()),    );  }}